import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  width: 300px;
  height: 400px;
  background-color: white;
  box-shadow: 0 0 10px rgba(0,0,0,0.1);
  display: flex;
  flex-direction: column;
`;

const MessageContainer = styled.div`
  padding: 10px;
  overflow-y: auto;
  flex-grow: 1;
`;

const InputContainer = styled.div`
  padding: 10px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  box-sizing: border-box;
`;

const Message = styled.div`
  background-color: ${props => props.sender === 'user' ? '#dcf8c6' : '#f3f3f3'};
  border-radius: 10px;
  margin: 5px;
  padding: 8px 10px;
  max-width: 80%;
  align-self: ${props => props.sender === 'user' ? 'flex-end' : 'flex-start'};
  display: flex;
  flex-direction: column;
`;

const SenderLabel = styled.div`
  font-size: 0.75em;
  color: ${props => props.sender === 'user' ? 'gray' : 'blue'};
  margin-bottom: 4px;
`;


function Chatbox() {
  const [messages, setMessages] = useState([]);

  const sendMessage = (msg) => {
    setMessages([...messages, { text: msg, sender: 'user' }]);
    // Simulate an AI response
    setTimeout(() => {
      setMessages(m => [...m, { text: "This is an AI response to: " + msg, sender: 'ai' }]);
    }, 500); // delay the AI response by 500 ms for effect
  };


  return (
    <Container>
      <MessageContainer>
        {messages.map((m, index) => (
            <Message key={index} sender={m.sender}>
            <SenderLabel sender={m.sender}>
                {m.sender === 'user' ? 'You' : 'E-Advisor'}
            </SenderLabel>
            {m.text}
            </Message>
        ))}
      </MessageContainer>

      <InputContainer>
        <Input placeholder="Type your message here..." onKeyDown={(e) => {
          if (e.key === 'Enter') {
            sendMessage(e.target.value);
            e.target.value = ''; // clear the input after sending
          }
        }} />
      </InputContainer>
    </Container>
  );
}

export default Chatbox;
