import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Chatbox.css'; // Import CSS

function Chatbox() {
  const [messages, setMessages] = useState([]);

  // useEffect to add initial AI message
  useEffect(() => {
    const welcomeMessage = {
      text: "Hello, welcome to UMD's Virtual Advising.\nTo get started, upload your unofficial transcript. If having trouble finding it, you can go to https://www.testudo.umd.edu to retrieve it.",
      sender: 'ai',
      label: 'E-Advisor'
    };
    setMessages([welcomeMessage]);
  }, []);

  const sendMessage = async (msg) => {
    const newMessages = [...messages, { text: msg, sender: 'user', label: 'You' }];
    setMessages(newMessages);
    try {
      const response = await axios.post('http://localhost:3001/api/chat', {
        messages: [{ role: "user", content: msg }],
        model: "mixtral-8x7b-32768"
      });

      const aiMessage = response.data.response;
      setMessages([...newMessages, { text: aiMessage, sender: 'ai', label: 'E-Advisor' }]);
    } catch (error) {
      console.error('Error communicating with the chat API:', error);
      setMessages([...newMessages, { text: "Failed to get a response.", sender: 'ai', label: 'E-Advisor' }]);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await axios.post('http://localhost:3001/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'  // This header might be automatically set by browsers
          }
        });
        alert('File uploaded successfully!');
      } catch (error) {
          console.error('Error uploading file:', error);
          alert('Failed to upload file.');
      }
    }
  };


  return (
    <div className="container">
      <div className="messageContainer">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            <span className={`senderLabel ${message.sender}`}>{message.label}</span>
            {message.text}
          </div>
        ))}
      </div>
      <div className="inputContainer">
        <input type="file" onChange={handleFileUpload} />
        <input
          className="input"
          placeholder="Type your message here..."
          onKeyDown={(e) => {
            if (e.key === 'Enter' && e.target.value.trim()) {
              sendMessage(e.target.value);
              e.target.value = ''; // Clear the input after sending
            }
          }}
        />
      </div>
    </div>
  );
}

export default Chatbox;
