import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  width: 300px;
  height: 400px;
  background-color: white;
  box-shadow: 0 0 10px rgba(0,0,0,0.1);
  display: flex;
  flex-direction: column;
`;

const MessageContainer = styled.div`
  padding: 10px;
  overflow-y: auto;
  flex-grow: 1;
`;

const InputContainer = styled.div`
  padding: 10px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  box-sizing: border-box;
`;

function Chatbox() {
  const [messages, setMessages] = useState([]);

  const sendMessage = (msg) => {
    setMessages([...messages, { text: msg, sender: 'user' }]);
    // Here you would add the logic to send a message to your backend or API
  };

  return (
    <Container>
      <MessageContainer>
        {messages.map((m, index) => (
          <div key={index}>{m.text}</div>
        ))}
      </MessageContainer>
      <InputContainer>
        <Input placeholder="Type your message here..." onKeyDown={(e) => {
          if (e.key === 'Enter') {
            sendMessage(e.target.value);
            e.target.value = ''; // clear the input after sending
          }
        }} />
      </InputContainer>
    </Container>
  );
}

export default Chatbox;
