import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const Container = styled.div`
  position: fixed;
  bottom: 10%;
  right: 10%;
  left: 10%;
  top: 10%;
  width: auto;
  height: auto;
  background-color: white;
  box-shadow: 0 0 20px rgba(0,0,0,0.2);
  display: flex;
  flex-direction: column;
  border-radius: 10px;
  overflow: hidden;
`;

const MessageContainer = styled.div`
  padding: 20px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  flex-grow: 1;
`;

const InputContainer = styled.div`
  padding: 20px;
  background: #f8f8f8;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 20px;
  box-sizing: border-box;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Message = styled.div`
  background-color: ${props => props.sender === 'user' ? '#dcf8c6' : '#f3f3f3'};
  padding: 10px;
  margin: 5px;
  border-radius: 8px;
  align-self: ${props => props.sender === 'user' ? 'flex-end' : 'flex-start'};
  display: flex;
  flex-direction: column;
  max-width: 80%;
  text-align: left;  // Ensure text within the bubble is left-aligned
`;

const SenderLabel = styled.span`
  font-size: 0.8em;
  color: ${props => props.sender === 'user' ? '#4a4a4a' : '#4a4a4a'};
  margin-bottom: 4px;
  align-self: ${props => props.sender === 'user' ? 'flex-end' : 'flex-start'};
`;


function Chatbox() {
  const [messages, setMessages] = useState([]);
  const sendMessage = async (msg) => {
    const newMessages = [...messages, { text: msg, sender: 'user', label: 'You' }];
    setMessages(newMessages);

    try {
      const response = await axios.post('http://localhost:3001/api/chat', {
        messages: [{ role: "user", content: msg }],
        model: "mixtral-8x7b-32768"
      });

      const aiMessage = response.data.response;
      setMessages([...newMessages, { text: aiMessage, sender: 'ai', label: 'E-Advisor' }]);
    } catch (error) {
      console.error('Error communicating with the chat API:', error);
      setMessages([...newMessages, { text: "Failed to get a response.", sender: 'ai', label: 'E-Advisor' }]);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await axios.post('http://localhost:3001/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'  // This header might be automatically set by browsers
          }
        });
        alert('File uploaded successfully!');
      } catch (error) {
          console.error('Error uploading file:', error);
          alert('Failed to upload file.');
      }
    }
  };


  return (
    <Container>
      <MessageContainer>
        {messages.map((message, index) => (
          <Message key={index} sender={message.sender}>
            <SenderLabel sender={message.sender}>{message.label}</SenderLabel>
            {message.text}
          </Message>
        ))}
      </MessageContainer>
      <InputContainer>
        <input type="file" onChange={handleFileUpload} />
        <Input
          placeholder="Type your message here..."
          onKeyDown={(e) => {
            if (e.key === 'Enter' && e.target.value.trim()) {
              sendMessage(e.target.value);
              e.target.value = ''; // Clear the input after sending
            }
          }}
        />
      </InputContainer>
    </Container>
  );
}

export default Chatbox;
