import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const Container = styled.div`
  position: fixed;
  bottom: 10%;
  right: 10%;
  left: 10%;
  top: 10%;
  width: auto;
  height: auto;
  background-color: white;
  box-shadow: 0 0 20px rgba(0,0,0,0.2);
  display: flex;
  flex-direction: column;
  border-radius: 10px;
  overflow: hidden;
`;

const MessageContainer = styled.div`
  padding: 20px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  flex-grow: 1;
`;

const InputContainer = styled.div`
  padding: 20px;
  background: #f8f8f8;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 20px;
  box-sizing: border-box;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Message = styled.div`
  background-color: ${props => props.sender === 'user' ? '#dcf8c6' : '#f3f3f3'};
  border-radius: 10px;
  margin: 5px;
  padding: 10px 15px;
  max-width: 80%;
  width: fit-content;
  align-self: ${props => props.sender === 'user' ? 'flex-end' : 'flex-start'};
`;

const SenderLabel = styled.div`
  font-size: 0.75em;
  color: ${props => props.sender === 'user' ? 'gray' : 'blue'};
  margin-bottom: 4px;
`;

function Chatbox() {
  const [messages, setMessages] = useState([]);

  const sendMessage = async (msg) => {
    setMessages(messages => [...messages, { text: msg, sender: 'user' }]);

    try {
      const response = await axios.post('http://localhost:3001/chat/completions', {
          messages: [
              {
                  "role": "user",
                  "content": msg,
              }
          ],
          model: "mixtral-8x7b-32768",
      });

      setMessages(messages => [...messages, { text: response.data.choices[0].message.content, sender: 'ai' }]);
    } catch (error) {
      console.error('Error calling Groq API:', error);
      setMessages(messages => [...messages, { text: "Failed to get a response from E-Advisor.", sender: 'ai' }]);
    }
  };

  return (
    <Container>
      <MessageContainer>
        {messages.map((m, index) => (
          <Message key={index} sender={m.sender}>
            <SenderLabel sender={m.sender}>
              {m.sender === 'user' ? 'You' : 'E-Advisor'}
            </SenderLabel>
            {m.text}
          </Message>
        ))}
      </MessageContainer>
      <InputContainer>
        <Input
          placeholder="Type your message here..."
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              sendMessage(e.target.value);
              e.target.value = ''; // Clear the input after sending
            }
          }}
        />
      </InputContainer>
    </Container>
  );
}

export default Chatbox;
